public class AreaOfTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//5.	Write a program to find the area of a  triangle
		
		//Formula of Area of Triangle = 1/2 (Height*Base)
		
		int b= 4;
		int h= 3;
		int area = (b*h)/2;
		
		
		
		System.out.println(area);
		System.out.println("Area of Triangle is the multiplicaiton of Height and Base divided by 2: " +area);
				
	}

}
